package online.memphis.util;public enum CalendarUtil {    JANUARY,    FEBRUARY,    MARCH,    APRIL,    MAY,    JUNE,    JULY,    AUGUST,    SEPTEMBER,    OCTOBER,    NOVEMBER,    DECEMBER;    public static int getDays(CalendarUtil month, boolean leapYear) throws IllegalAccessException {        int days;        switch (month) {            case JANUARY:            case MARCH:            case MAY:            case JULY:            case AUGUST:            case OCTOBER:            case DECEMBER: {                days = 31;                break;            }            case APRIL:            case JUNE:            case SEPTEMBER:            case NOVEMBER: {                days = 30;                break;            }            case FEBRUARY: {                days = leapYear ? 29 : 28;                break;            }            default: throw new IllegalAccessException(month.toString());        }        return days;    }}